import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { ToastContainer, toast } from "react-toastify";
import Pglist from './Pglist';
import "../css/common.css";
import "../css/property_list.css";
import { useParams } from 'react-router-dom';


const Delhi = () => {    
  const [pglists,setpglists] = useState([]);

  const{cityName} = useParams();
  //console.log(window.location.pathname);

  var uri=window.location.pathname

  useEffect(() => {
    getallpglistfromcity();
  },[]);

  const buttonStyle1 = {
    marginRight: '40px',
    marginBottom: '30px',
    fontSize: '18px',
    padding: '10px 20px',
    borderRadius: '5px',
    cursor: 'pointer',
  };

  const handleButtonClickFemale=()=>{
    console.log("gender");
    axios.get(`http://localhost:8080/properties/gender/FEMALE`).then(

    (response) =>{
     console.log(response.data);

     setpglists(response.data)

    }, 
    (error) =>{
      console.log(error);

    }
 ) ;
  }

  const handleButtonClickMale=()=>{
    console.log("gender");
    axios.get(`http://localhost:8080/properties/gender/MALE`).then(

    (response) =>{
     console.log(response.data);

     setpglists(response.data)

    }, 
    (error) =>{
      console.log(error);

    }
 ) ;
  }

  const getallpglistfromcity =() => {
     axios.get(`http://localhost:8080/properties/list/`+cityName).then(

       (response) =>{
        console.log(response.data);

        setpglists(response.data)

       }, 
       (error) =>{
         console.log(error);

       }
    ) ;

}




   return (

<div className="container">

      <h1 className="text-center">Property Listings</h1>
      <br></br>
      <button style={buttonStyle1} onClick={()=>handleButtonClickFemale()}> Sort By Female  </button>

        <button style={buttonStyle1} onClick={()=>handleButtonClickMale()}> Sort By Male  </button>

        <div>   
            { pglists.length>0
                ? pglists.map((item) => <Pglist key ={item.id} pglist = {item } />)
                    :"No courses"}       
        </div>

    </div>
  );
};

export default Delhi;
