import React, { useState } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap CSS

const PropWFacility = () => {
  const [Name, setName] = useState('');
  const [address, setAddress] = useState('');
  const [rent, setRent] = useState('');
  const [gender, setGender] = useState('');
  const [description, setDescription] = useState('');
  const [ratingClean, setRatingClean] = useState('');
  const [ratingFood, setRatingFood] = useState('');
  const [ratingSafety, setRatingSafety] = useState('');
  const [cityName, setCityName] = useState('');

  const [facilities, setFacilities] = useState([
    {
      name: '',
      type: '',
    },
  ]);

  const [errorMessage, setErrorMessage] = useState('');

  const handleFacilityChange = (e, index) => {
    const { name, value } = e.target;
    const updatedFacilities = [...facilities];
    updatedFacilities[index][name] = value;
    setFacilities(updatedFacilities);
  };

  const addFacility = () => {
    setFacilities([...facilities, { name: '', type: '' }]);
  };

  const removeFacility = (index) => {
    const updatedFacilities = [...facilities];
    updatedFacilities.splice(index, 1);
    setFacilities(updatedFacilities);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const propertyData = {
      name: Name,
      address,
      rent,
      gender,
      description,
      ratingClean,
      ratingFood,
      ratingSafety,
      cityName,
      facilities,
    };

    axios
      .post('http://localhost:8080/admin/propertyfacility', propertyData)
      .then((response) => {
        // Handle success
        console.log('Data added successfully:', response.data);
        // You can redirect or perform other actions here
      })
      .catch((error) => {
        // Handle error
        console.error('Error adding data:', error);
        setErrorMessage('An error occurred while adding data.');
      });
  };

  return (
    <div
      className="container-fluid p-0"
      style={{
        background: 'download.jpeg',
        backgroundSize: 'cover',
        backgroundRepeat: 'no-repeat',
        minHeight: '100vh',
      }}
    >
      <div className="container-fluid p-0">
      <div className="container mt-5">
        <div className="row">
          <div className="col-md-6 offset-md-3">
            <div className="card">
              <div className="card-header bg-primary text-white">
                <h1>Add Data</h1>
              </div>
              <div className="card-body">
                <form onSubmit={handleSubmit}>
                  <div className="mb-3">
                    <label className="form-label">Name</label>
                    <input
                      type="text"
                      className="form-control"
                      value={Name}
                      onChange={(e) => setName(e.target.value)}
                      placeholder="Name"
                      required
                    />
                  </div>
                  <div className="mb-3">
                    <label className="form-label">Description</label>
                    <input
                      type="text"
                      className="form-control"
                      value={description}
                      onChange={(e) => setDescription(e.target.value)}
                      placeholder="Description"
                    />
                  </div>
                  <div className="mb-3">
                    <label className="form-label">Address</label>
                    <input
                      type="text"
                      className="form-control"
                      value={address}
                      onChange={(e) => setAddress(e.target.value)}
                      placeholder="Address"
                    />
                  </div>
                  <div className="mb-3">
                    <label className="form-label">Gender</label>
                    <input
                      type="text"
                      className="form-control"
                      value={gender}
                      onChange={(e) => setGender(e.target.value)}
                      placeholder="Gender"
                    />
                  </div> 
                  <div className="mb-3">
                    <label className="form-label">Rent</label>
                    <input
                      type="text"
                      className="form-control"
                      value={rent}
                      onChange={(e) => setRent(e.target.value)}
                      placeholder="Rent"
                    />
                  </div>
                  <div className="mb-3">
                    <label className="form-label">RatingClean</label>
                    <input
                      type="text"
                      className="form-control"
                      value={ratingClean}
                      onChange={(e) => setRatingClean(e.target.value)}
                      placeholder="RatingClean"
                    />
                  </div>  <div className="mb-3">
                    <label className="form-label">RatingFood</label>
                    <input
                      type="text"
                      className="form-control"
                      value={ratingFood}
                      onChange={(e) => setRatingFood(e.target.value)}
                      placeholder="RatingFood"
                    />
                  </div>
                  <div className="mb-3">
                    <label className="form-label">RatingSafety</label>
                    <input
                      type="text"
                      className="form-control"
                      value={ratingSafety}
                      onChange={(e) => setRatingSafety(e.target.value)}
                      placeholder="RatingSafety"
                    />
                  </div>
                  <div className="mb-3">
                    <label className="form-label">CityName</label>
                    <input
                      type="text"
                      className="form-control"
                      value={cityName}
                      onChange={(e) => setCityName(e.target.value)}
                      placeholder="CityName"
                    />
                  </div>
                  <div className="mb-3">
                    <label className="form-label">Facilities</label>
                    {facilities.map((facility, index) => (
                      <div key={index} className="d-flex mb-2">
                        <input
                          type="text"
                          className="form-control"
                          value={facility.name}
                          onChange={(e) => handleFacilityChange(e, index)}
                          placeholder="Facility Name"
                        />
                        <input
                          type="text"
                          className="form-control"
                          value={facility.type}
                          onChange={(e) => handleFacilityChange(e, index)}
                          placeholder="Facility Type"
                        />
                        <button
                          type="button"
                          className="btn btn-danger"
                          onClick={() => removeFacility(index)}
                        >
                          Remove
                        </button>
                      </div>
                    ))}
                    <button
                      type="button"
                      className="btn btn-success"
                      onClick={addFacility}
                    >
                      Add Facility
                    </button>
                  </div>
                  <button type="submit" className="btn btn-primary">
                    Add Data
                  </button>
                </form>
                {errorMessage && (
                  <p className="text-danger mt-3">{errorMessage}</p>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
    </div>
  );
};

export default PropWFacility;
