import React, { useState, useEffect } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css'; 
import { useNavigate, useParams } from 'react-router-dom';


const FacilitiesChange= () => {
  const navigate = useNavigate();

  const [data, setData] = useState([]);
  const [isEditing, setIsEditing] = useState(null);
  const item_value = sessionStorage.getItem("id");

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = () => {
    axios
      .get('http://localhost:8080/facilities')
      .then((response) => {
        navigate('/facilities')

        setData(response.data);

      })
      .catch((error) => {});
  };

  const handleEdit = (propertyId) => {
    setIsEditing(propertyId);
  };

  const handleSave = (editedProperty) => {
    axios
      .put('http://localhost:8080/admin/facilities/edit', editedProperty)
      .then(() => {
        fetchData();
      })
      .catch((error) => {});
  };

  // const containerStyle ={
  //   backgroundImage: "/pg.webp"
  // }

  return (
    <div className="container mt-5">
      <br></br>
      <br></br>
      <h1 className="text-center">Facilities Listings</h1>
      <table className="table table-bordered table-striped">
        <thead className="thead-dark">

          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Type</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item) => (
            <tr key={item.id} >
              <td>{item.id}</td>
              <td style={{width:"270px"}}>
                {isEditing === item.id ? (
                  <input style={{width:"180px"}}
                    type="text"
                    value={item.name}
                    onChange={(e) =>
                      setData((prevProperties) =>
                        prevProperties.map((prevProperty) =>
                          prevProperty.id === item.id
                            ? { ...prevProperty, name: e.target.value }
                            : prevProperty
                        )
                      )
                    }GetDataPage1
                  />
                ) : (
                  item.name
                )}
              </td>

              <td>
                {isEditing === item.id ? (
                  <input style={{width:"450px"}}
                    type="text"
                    value={item.type}
                    onChange={(e) =>
                      setData((prevProperties) =>
                        prevProperties.map((prevProperty) =>
                          prevProperty.id === item.id
                            ? { ...prevProperty, type: e.target.value }
                            : prevProperty
                        )
                      )
                    }
                  />
                ) : (
                  item.type
                )}
              </td>


              <td>
                {isEditing === item.id ? (
                  <>
                    <button
                      className="btn btn-primary btn-sm"
                      onClick={() => handleSave(item)}
                    >
                      Update
                    </button>
                    <button
                      className="btn btn-secondary btn-sm"
                      onClick={() => setIsEditing(null)}
                    >
                      Cancel
                    </button>
                  </>
                ) : (
                  <>
                    <button
                      className="btn btn-info btn-sm"
                      onClick={() => handleEdit(item.id)}
                    >
                      Edit
                    </button> 


                  </>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default FacilitiesChange;
