      // .then((response) => {
      //   debugger;
      //   // get the server result
      //   const result = response.data;
      //   console.log(result);

       

      //   if (result.data != null) {
      //     debugger;
      //     toast.success("Welcome to the PGManagement");

      //     // get the data sent by server
      //   //  const { id, name,email, password } = result["data"];
          
      //   //  persist the logged in user's information for future use
      //   // window.sessionStorage.setItem("id",result.id);
      //   //   sessionStorage["name"] = name;
      //   //   sessionStorage["email"] = email;
      //   //   sessionStorage["role"] = "User";
      //   //   sessionStorage["loginStatus"] = 1;

      //     // navigate to home component
      //     navigate("/Home");
      //   } else {
      //     toast.error("Invalid user name or password");
      //   }
      // });
    }
  };

  return (
    <div>
      <br/><br/><br/><br/><br/><br/>
      <div className="container col-4 mt-4 px-5 pb-5 shadow bg-body rounded">
        <div className="row justify-content-center">
          <div className="heading">User Signin</div>
          <div className="col">
            <div className="mb-3">
              <label htmlFor="exampleInputEmail1" className="form-label">
                Email address
              </label>
              <input
                onChange={(e) => {
                  setEmail(e.target.value);
                }}
                type="email"
                className="form-control"
                id="exampleInputEmail1"
                aria-describedby="emailHelp"
              />
              <div id="emailHelp" className="form-text">
                We'll never share your email with anyone else.
              </div>
            </div>
            <div className="mb-3">
              <label htmlFor="exampleInputPassword1" className="form-label">
                Password
              </label>
              <input
                onChange={(e) => {
                  setPassword(e.target.value);
                }}
                type="password"
                className="form-control"
                id="exampleInputPassword1"
              />
            </div>

            <button
              onClick={signinPerson}
              className=" w-100 btn btn btn-outline-dark"
            >
              Signin
            </button>
            <div className="form-label mt-2">
              Not yet registered?, <Link to="/signup">Register Here</Link>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Signin;
